import React, { useEffect, useState } from "react";

import Login from "./components/Login/Login";
import Home from "./components/Home/Home";
import MainHeader from "./components/MainHeader/MainHeader";

function App() {
  // const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  // console.log("isLoggedIn", isLoggedIn);
  // console.log(typeof localStorage.getItem("statusLogin"));

  // useEffect(() => {
  //   const handleMouseEvent = (event) => {
  //     setPosition({ x: event.clientX, y: event.clientY });
  //   };
  //   document.addEventListener("mousemove", handleMouseEvent);
  //   return () => document.removeEventListener("mousemove", handleMouseEvent);
  // }, []);

  useEffect(() => {
    // console.log("first h1");
    if (localStorage.getItem("statusLogin") === "1") {
      setIsLoggedIn(true);
    }
    // console.log("hi1");
  }, []);

  // console.log("hi2");
  const loginHandler = (email, password) => {
    // We should of course check email and password
    // But it's just a dummy/ demo anyways
    if (email === "abdulkerim@d.com" && password === "123456789") {
      localStorage.setItem("statusLogin", 1);
      setIsLoggedIn(true);
    } else {
      alert("wrong password or email");
    }
  };

  const logoutHandler = () => {
    localStorage.removeItem("statusLogin");
    setIsLoggedIn(false);
  };

  return (
    <React.Fragment>
      <MainHeader isAuthenticated={isLoggedIn} onLogout={logoutHandler} />
      <main>
        {!isLoggedIn && <Login onLogin={loginHandler} />}
        {isLoggedIn && <Home onLogout={logoutHandler} />}
      </main>
    </React.Fragment>
  );
}

export default App;
