import React, { useEffect, useReducer, useState } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const ACTION = {
  INPUT_CHANGE: "input_change",
  INPUT_ISVALID: "input_isValid",
};

const reducerEmail = (emailState, action) => {
  switch (action.type) {
    case ACTION.INPUT_CHANGE: {
      return (emailState = { ...emailState, value: action.Payload.value1 });
    }
    case ACTION.INPUT_ISVALID: {
      return (emailState = {
        ...emailState,
        isValid: emailState.value.includes("@") ? true : false,
      });
    }
    default:
      return emailState.value;
  }
};

const reducerPassword = (passwordState, action) => {
  switch (action.type) {
    case ACTION.INPUT_CHANGE:
      return (passwordState = {
        ...passwordState,
        value: action.Payload.value2,
      });
    case ACTION.INPUT_ISVALID:
      return (passwordState = {
        ...passwordState,
        isValid: passwordState.value.trim().length > 0 ? true : false,
      });
  }
};

const Login = (props) => {
  const [emailState, dispatchEmail] = useReducer(reducerEmail, {
    value: "",
    isValid: true,
  });

  const [passwordState, dispatchPassword] = useReducer(reducerPassword, {
    value: "",
    isValid: true,
  });

  // const [enteredEmail, setEnteredEmail] = useState("");
  // const [emailIsValid, setEmailIsValid] = useState();
  // const [enteredPassword, setEnteredPassword] = useState("");
  // const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const emailChangeHandler = (event) => {
    dispatchEmail({
      type: ACTION.INPUT_CHANGE,
      Payload: { value1: event.target.value },
    });
    // setEnteredEmail(event.target.value);
  };

  const passwordChangeHandler = (event) => {
    // setEnteredPassword(event.target.value);
    dispatchPassword({
      type: ACTION.INPUT_CHANGE,
      Payload: { value2: event.target.value },
    });
  };

  useEffect(() => {
    console.log("time1");

    const timeoutID = setTimeout(() => {
      console.log("time2");
      setFormIsValid(emailState.isValid && passwordState.isValid);
    }, 2000);

    return () => {
      console.log("returnPart");
      clearTimeout(timeoutID);
    };
  }, [passwordState.value, emailState.value]);

  const validatePasswordHandler = () => {
    // setPasswordIsValid(enteredPassword.trim().length > 6);
    dispatchPassword({ type: ACTION.INPUT_ISVALID });
  };

  const validateEmailHandler = () => {
    dispatchEmail({ type: ACTION.INPUT_ISVALID });
    // setEmailIsValid(emailState.value.includes("@"));
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, passwordState.value);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.value}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
